def getIP(): # 获取当前机器主网卡ip
    hostname = socket.gethostname()
    ip = socket.gethostbyname(hostname)
    return ip


def chat_whoami():      # 给出当前机器主网卡ip的最后一段数字
    ip = getIP()
    ip_last_digit = ip.split(".")[-1]
    StuID = chat_IP2StuID(ip_last_digit)
    return StuID


def chat_StuID2IP(StuID: int):      # 给出学生号数,求出ip最后一段
    if StuID / 6 == StuID // 6: 
        OP = StuID / 6
        ip_last_digit = 60 + OP
    else:
        R = StuID % 6
        TP = R * 10
        OP = StuID // 6 + 1
        ip_last_digit = TP + OP
        # 求十位时,其为原号数除以6的余数（R）乘10,TP:十位
        # 求个位时,其为原号数除以6,向下取整,然后加1,OP:个位
    result = int(ip_last_digit)
    return result

def chat_IP2StuID(ip_last_digit):
    m = int(ip_last_digit)
    o = m - 1
    result = (m - o // 10 * 10) * 6 - (6 - (o // 10))
    return result

def get_IP_prefix(ip_address):
    # 分割IP地址为各个部分
    parts = ip_address.split(".")
    # 移除最后一部分
    parts[:-1]
    # 将剩余部分重新组合成IP地址
    return ".".join(parts[:-1]) + "."


